import React,{ useState,useEffect} from 'react';
import NewsCard from '../../NewsCard/NewsCard'
import { Spinner } from 'react-bootstrap';
function HomeView(props) {
    const[isLoading,setLoading] = useState(true);
    const [data, setData] = useState([]);
    const getNews = async () => {
      try {
       const response = await fetch(`https://newsapi.org/v2/everything?q=${props.search}&from=2021-12-05&sortBy=popularity&apiKey=610f3044f4c54d95b5f807c9c7233a2c`);
       const json = await response.json();
       console.log(json);
       setData(json.articles);
     } catch (error) {
       console.error(error);
     } finally {
       setLoading(false);
     }
}
    useEffect(() => {
    getNews();
   });
   if (isLoading){
    return <div><Spinner animation="border" /></div>
  }
  else{
    return data.map(item => <NewsCard urlToImage={item.urlToImage} title ={item.title}/>)
    }
}

export default HomeView;
