import React,{ useState,useEffect} from 'react';
import NewsCard from '../../NewsCard/NewsCard'
import { Spinner } from 'react-bootstrap';
function HomeView() {
    const[isLoading,setLoading] = useState(false);
    const [data, setData] = useState([]);
    const getNews = async (props) => {
      try {
       const response = await fetch(`https://newsapi.org/v2/top-headlines?country=${props.region}&apiKey=610f3044f4c54d95b5f807c9c7233a2c`);
       const json = await response.json();
       setData(json.articles);
     } catch (error) {
       console.error(error);
     } finally {
       setLoading(false);
     }
}
    useEffect(() => {
    getNews('in');
   }, []);
   if (isLoading){
    return <Spinner animation="border" />
}
else{
    return data.map((item) => <NewsCard urlToImage={item.urlToImage} title ={item.title}/>)
    }
}

export default HomeView;
