import React, { useEffect,useState } from 'react';
import NewsCard from '../../NewsCard/NewsCard';
import { Spinner } from 'react-bootstrap';
function Headline(props) {
    const[isLoading,setLoading] = useState(true);
    const [HeadlineData, setHeadlineData] = useState([]);
    const getHeadlines = async () => {
      try {
       const response = await fetch(`https://newsapi.org/v2/top-headlines?country=${props.region}&apiKey=610f3044f4c54d95b5f807c9c7233a2c`);
       const json = await response.json();
       setHeadlineData(json.articles);
     } catch (error) {
       console.error(error);
     } finally {
       setLoading(false);
     }
}
useEffect(() => {
    getHeadlines();
});
if(isLoading){
    return <Spinner animation="border" />
}
else {
    return (
      HeadlineData.map(item =><NewsCard urlToImage={item.urlToImage} title ={item.title}/>)
    );
}  
}

export default Headline;
